## 🧾**Celoten program v C++**

```cpp
#include <iostream>
#include <string>
#include <windows.h>
#include <shlobj.h>
#include <FreeImage.h>
#include <ft2build.h>
#include FT_FREETYPE_H

const int ŠIRINA = 1920;
const int VIŠINA = 1080;

bool ustvariSlikoZBesedilom(const std::string& citat, const std::string& potDoSlikovneDatoteke) {
    // Inicializacija FreeImage
    FreeImage_Initialise();

    // Ustvarjanje 32-bitne RGBA slike
    FIBITMAP* dib = FreeImage_Allocate(ŠIRINA, VIŠINA, 32);
    if (!dib) {
        std::cerr << "Neuspešno ustvarjanje slike." << std::endl;
        return false;
    }

    // Polnjenje ozadja s črno barvo
    for (int y = 0; y < VIŠINA; ++y) {
        BYTE* pikice = FreeImage_GetScanLine(dib, y);
        for (int x = 0; x < ŠIRINA; ++x) {
            pikice[0] = 0;  // B
            pikice[1] = 0;  // G
            pikice[2] = 0;  // R
            pikice[3] = 255;  // A
            pikice += 4;
        }
    }

    // Nastavitev FreeType
    FT_Library ft;
    if (FT_Init_FreeType(&ft)) {
        std::cerr << "Ne morem inicializirati FreeType knjižnice" << std::endl;
        return false;
    }

    // Nalaganje pisave
    FT_Face obraz;
    if (FT_New_Face(ft, "C:\\Windows\\Fonts\\arial.ttf", 0, &obraz)) {
        std::cerr << "Neuspešno nalaganje pisave" << std::endl;
        return false;
    }

    FT_Set_Pixel_Sizes(obraz, 0, 48);

    // Upodobitev besedila
    int širinaBesede = 0;
    for (char črka : citat) {
        if (FT_Load_Char(obraz, črka, FT_LOAD_RENDER)) continue;
        širinaBesede += obraz->glyph->advance.x >> 6;
    }

    int x = (ŠIRINA - širinaBesede) / 2;
    int y = VIŠINA / 2;

    for (char črka : citat) {
        if (FT_Load_Char(obraz, črka, FT_LOAD_RENDER)) continue;
        FT_GlyphSlot glyph = obraz->glyph;

        for (int vrstica = 0; vrstica < glyph->bitmap.rows; ++vrstica) {
            for (int stolpec = 0; stolpec < glyph->bitmap.width; ++stolpec) {
                BYTE* pixel = FreeImage_GetScanLine(dib, y - glyph->bitmap_top + vrstica) +
                              4 * (x + glyph->bitmap_left + stolpec);
                BYTE vrednost = glyph->bitmap.buffer[vrstica * glyph->bitmap.pitch + stolpec];
                pixel[0] = pixel[1] = pixel[2] = 255;
                pixel[3] = vrednost;
            }
        }

        x += glyph->advance.x >> 6;
    }

    // Shranjevanje slike
    if (!FreeImage_Save(FIF_PNG, dib, potDoSlikovneDatoteke.c_str(), 0)) {
        std::cerr << "Neuspešno shranjevanje slike." << std::endl;
        return false;
    }

    FreeImage_Unload(dib);
    FreeImage_DeInitialise();
    FT_Done_Face(obraz);
    FT_Done_FreeType(ft);

    return true;
}

bool nastaviOzadje(const std::string& potDoSlike) {
    std::wstring širokaPot(potDoSlike.begin(), potDoSlike.end());
    return SystemParametersInfoW(SPI_SETDESKWALLPAPER, 0, (void*)širokaPot.c_str(), SPIF_UPDATEINIFILE | SPIF_SENDCHANGE);
}

int main() {
    std::string citat;
    std::cout << "Vnesite svoj citat: ";
    std::getline(std::cin, citat);

    std::string potDoSlike = "C:\\wallpaper.png";

    if (!ustvariSlikoZBesedilom(citat, potDoSlike)) {
        std::cerr << "Neuspešno ustvarjanje slike ozadja." << std::endl;
        return 1;
    }

    if (!nastaviOzadje(potDoSlike)) {
        std::cerr << "Neuspešno nastavljeno ozadje." << std::endl;
        return 1;
    }

    std::cout << "Ozadje uspešno nastavljeno!" << std::endl;
    return 0;
}
```

