const { chromium } = require('playwright');
const fs = require('fs');
const path = require('path');

const COOKIES_FILE = path.join(__dirname, 'cookies.json');

async function saveCookies(context) {
  const cookies = await context.cookies();
  fs.writeFileSync(COOKIES_FILE, JSON.stringify(cookies, null, 2));
  console.log('✅ Cookies saved.');
}

async function loadCookies(context) {
  if (fs.existsSync(COOKIES_FILE)) {
    const cookies = JSON.parse(fs.readFileSync(COOKIES_FILE, 'utf8'));
    await context.addCookies(cookies);
    console.log('🍪 Cookies loaded.');
    return true;
  }
  return false;
}

async function loginAndScrape() {
  const browser = await chromium.launch({ headless: false }); // set to true for headless
  const context = await browser.newContext();

  const loggedIn = await loadCookies(context);
  const page = await context.newPage();

  if (!loggedIn) {
    // Replace with your login logic
    await page.goto('https://example.com/login', { waitUntil: 'networkidle' });
    await page.fill('#username', 'your-username');
    await page.fill('#password', 'your-password');
    await page.click('#login-button');

    // Wait for navigation or some indicator of successful login
    await page.waitForNavigation({ waitUntil: 'networkidle' });

    // Save cookies after login
    await saveCookies(context);
  } else {
    await page.goto('https://example.com/dashboard', { waitUntil: 'networkidle' });
  }

  // Example scraping after login
  const pageTitle = await page.title();
  console.log('📄 Page Title:', pageTitle);

  // Close browser
  await context.close();
  await browser.close();
}

loginAndScrape().catch(console.error);
