<canvas id="rainCanvas"></canvas>

<script>
  const canvas = document.getElementById("rainCanvas");
  const ctx = canvas.getContext("2d");

  // Prilagoditev velikosti platna, da bo napolnil okno
  function resizeCanvas() {
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
  }
  window.addEventListener('resize', resizeCanvas);
  resizeCanvas();

  // Ustvarite plasti dežnih kapljic
  const layers = [
    { hitrost: 1, gostota: 0.5, velikost: 1, barve: "rgba(100, 140, 255, 0.5)" },
    { hitrost: 2, gostota: 0.7, velikost: 2, barve: "rgba(130, 160, 255, 0.6)" },
    { hitrost: 4, gostota: 1.0, velikost: 3, barve: "rgba(170, 200, 255, 0.8)" }
  ];

  const dezneKaplje = [];

  function ustvariDezneKaplje() {
    dezneKaplje.length = 0; // Odstrani prejšnje
    layers.forEach(layer => {
      const count = Math.floor(canvas.width * layer.gostota);
      for (let i = 0; i < count; i++) {
        dezneKaplje.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          dolžina: Math.random() * 20 + 10,
          širina: layer.velikost,
          barve: layer.barve,
          hitrost: layer.hitrost,
          indeksPlasti: dezneKaplje.length
        });
      }
    });
  }

  function narišiDež() {
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    for (let kaplja of dezneKaplje) {
      ctx.beginPath();
      ctx.strokeStyle = kaplja.barve;
      ctx.lineWidth = kaplja.širina;
      ctx.moveTo(kaplja.x, kaplja.y);
      ctx.lineTo(kaplja.x, kaplja.y + kaplja.dolžina);
      ctx.stroke();

      // Premik kaplje
      kaplja.y += kaplja.hitrost;

      // Ponovno nastavi kapljo na vrh, če gre iz zaslona
      if (kaplja.y > canvas.height) {
        kaplja.y = -kaplja.dolžina;
        kaplja.x = Math.random() * canvas.width;
      }
    }

    requestAnimationFrame(narišiDež);
  }

  // Poženi
  ustvariDezneKaplje();
  narišiDež();
</script>
